        var y = +d3.select(this).attr('cy');
        dists.push(Math.hypot(point[0] - x, point[1] - y));
      });
      var index = dists.length;

      if (dists.length > 1) {
        var sorted = dists.slice(0).sort(function (a, b) {
          return a - b;
        });
        var closest = dists.indexOf(sorted[0]);
        var next = dists.indexOf(sorted[1]);

        if (closest <= next) {
          index = closest + 1;
