      // draw control points

      for (var i = 0; i <= l; i += inc) {
        var p = node.getPointAtLength(i);
        addRoutePoint(p);
      } // convert length to distance


      routeLength.innerHTML = Math.round(l * distanceScale.value) + ' ' + distanceUnit.value;
