      var anchor = icons.select('use[data-id=\'' + i + '\']');

      if (anchor.size()) {
        var size = anchor.attr('width');

        var xa = _.round(x - size * 0.47, 2);

        var ya = _.round(y - size * 0.47, 2);

        anchor.attr('transform', null).attr('x', xa).attr('y', ya);
      }

      cells[index].manor = i;
      cells[manors[i].cell].manor = undefined;
      manors[i].x = x, manors[i].y = y, manors[i].region = region, manors[i].cell = index;
